<?php namespace HashOver;chdir(realpath('../'));require('backend/json-setup.php');try{$hashover=new \HashOver('json');$hashover->setup->apiCheck('json');$hashover->setup->setupRemoteAccess();$hashover->setup->setPageURL('request');$hashover->initiate();$data=array();if($hashover->thread->totalCount>1){$hashover->parsePrimary();$hashover->parsePopular();$data['comments']=$hashover->comments;}else{$data=array('No comments.');}$hashover->statistics->executionEnd();$data['statistics']=array('execution-time'=>$hashover->statistics->executionTime,'script-memory'=>$hashover->statistics->scriptMemory,'system-memory'=>$hashover->statistics->systemMemory);echo Misc::jsonData($data);}catch(\Exception $error){echo Misc::displayException($error,'json');}